package Model;

public class Payment {
	private int id;
	private String name;
	
	public Payment() {
		// TODO Auto-generated constructor stub
	}

	public Payment(int id, String name) {
		super();
		this.id = id;
		this.name = name;
	}

	public int getId() {
		return id;
	}

	public void setId(int id) {
		this.id = id;
	}

	public String getName() {
		return name;
	}

	public void setName(String name) {
		this.name = name;
	}
	
	
	
}
